<template>  <div class="test">    <h1>{{ msg }}</h1>	<p> {{ url }} </p>	<p> {{ info }} </p>	<p> {{ info_complete }} </p>	<p> {{ specific_info }} </p>  </div></template><script>import axios from "axios";import configFile from "../services/test";export default {  name: 'test',  data () {    return {      msg: 'Change Your App',	  url: configFile.config.url,	  info: null,	  info_complete: null,	  specific_info: null    }  },    mounted () {	    axios	    .post(configFile.config.url, { email : "brittao@asglnz.onmicrosoft.com" , password : "secret"},{headers :  {'Content-Type': 'application/json'}})		//.post(configFile.config.url, { email : "britx@asglnz.onmicrosoft.com" , password : "badpassword"},{headers :  {'Content-Type': 'application/json'}})		.then(response => (			this.info_complete = response,			this.info = response.data,			this.specific_info = response.config.data,			console.log(response.data),			console.log(response.status),			console.log(response.statusText),			console.log(response.headers),			console.log(response.config)		))		.catch(error => (		  // The request was made and the server responded with a status code		  // that falls out of the range of 2xx		  console.log(error.response.data),		  console.log(error.response.status),		  console.log(error.response.headers)		))  }}</script>